import { useState } from "react";
import * as React from "react";
import {withAuthenticator} from "@aws-amplify/ui-react";
import "@aws-amplify/ui-react/styles.css";
import HealthMonitor from './HealthMonitor'
import S2sChatBot from './s2s'

function RecentVitals() {
  let vitals = [];
  try {
    vitals = JSON.parse(localStorage.getItem('carelink_vitals') || '[]');
  } catch (e) {
    vitals = [];
  }
  const latest = vitals.length > 0 ? vitals[vitals.length - 1] : null;

  if (!latest) return <div>No recent vitals available.</div>;

  return (
    <div style={{
      border: '1px solid #ccc',
      borderRadius: '8px',
      padding: '16px',
      marginBottom: '24px',
      maxWidth: '400px'
    }}>
      <h3>Most Recent Vitals</h3>
      <div><b>Time:</b> {new Date(latest.timestamp).toLocaleString()}</div>
      <div><b>Heart Rate:</b> {latest.heart_rate} bpm</div>
      <div><b>Blood Oxygen:</b> {latest.blood_oxygen} %</div>
      <div><b>Temperature:</b> {latest.temperature} Â°C</div>
    </div>
  );
}

const App = () => {
  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '40px', padding: '40px' }}>
      <RecentVitals />
      <HealthMonitor />
      <S2sChatBot />
    </div>
  );
}
export default App;
